package javaprograms;
import java.util.Scanner;
public class For_loop_Demo2 {
	public void getNum()
	{
		for (int i = 10; i>0; i--)
		{
			System.out.println(i);
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 For_loop_Demo2 obj = new For_loop_Demo2();
 obj.getNum();
	}

}
